import React, { useEffect, useState } from "react";

import { Button } from "native-base";

import { useNavigation } from "@react-navigation/native";

import { Container, FormInputs } from "./LoginScreen.styles";

import { useFormik } from "formik";

import { auth } from "../../firebase";

import { Alert } from "native-base";

import LoginSchema from "./LoginFormSchema";
import TextInput from "../../components/TextInput";

const LoginScreen = () => {

  const [loading, setLoading] = useState(false)

  const navigation = useNavigation();

  const { handleChange ,handleSubmit, values, handleBlur, errors, setErrors ,touched } =
    useFormik({
      initialValues: { email: "", password: "", onSubmit: '' },
      validationSchema: LoginSchema,

      onSubmit: async ({ email, password }) => {

        try {

          let userCredentials = await auth.signInWithEmailAndPassword(email, password)


        } catch (error) {

          // #TODO 
          // Handle specific error, use a popup instead of alert

          alert('Invalid username or password')
        }

      },
    });

  return (
    <Container behavior="padding">
      <FormInputs>
        <TextInput
          placeholder="Email"
          icon="email"
          onChangeText={handleChange("email")}
          onBlur={handleBlur("email")}
          error={errors.email}
          touched={touched.email}
        />

        <TextInput
          placeholder="Enter Password"
          icon="lock"
          type="password"
          onChangeText={handleChange("password")}
          onBlur={handleBlur("password")}
          error={errors.password}
          touched={touched.password}
        />

        <Button disabled={loading} style={{ marginTop: "3%" }} onPress={() => handleSubmit()}>
          { loading ? 'Loading..' : 'Sign in' }
        </Button>
        <Button
          style={{ marginTop: "3%" }}
          variant={"outline"}
          onPress={() => navigation.navigate("Register")}
        >
          Register
        </Button>
      </FormInputs>

      {loading && <Loading />}
    </Container>
  );
};

export default LoginScreen;
