import React, { useEffect, useState } from "react";

import { Input, Button } from "react-native-elements";

import { useNavigation } from "@react-navigation/native";

import { Container, FormInputs } from "./LoginScreen.styles";

import { useFormik } from "formik";

import { auth } from "../../firebase";

import LoginSchema from "./LoginFormSchema";
import TextInput from "../../components/TextInput";

const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigation = useNavigation();

  const { handleChange, handleSubmit, values, handleBlur, errors, touched } =
    useFormik({
      initialValues: { email: "", password: "" },
      validationSchema: LoginSchema,
      onSubmit: (values) =>
        alert(`Email: ${values.email}, Password: ${values.password}`),
    });

  return (
    <Container behavior="padding">
      <FormInputs>

        <TextInput  variant='underlined' size='xl' placeholder='Email' />

        <TextInput variant='underlined' size='xl' placeholder='Enter Password' />

        <Button
          containerStyle={{
            marginVertical: 10,
          }}
          onPress={() => handleSubmit()}
          title={"SIGN IN"}
        />
        <Button
             containerStyle={{
              marginVertical: 10,
            }}
          type="outline"

          onPress={() => navigation.navigate("Register")}
          title={"REGISTER"}
        />
      </FormInputs>
    </Container>
  );
};

export default LoginScreen;
