import { View, Text, ScrollView } from "react-native";
import React from "react";

import styled from "styled-components/native";

import { Card, Button, Icon } from "react-native-elements";
import { useNavigation } from "@react-navigation/native";

const SheltersScreen = () => {
  const dummyData = [
    {
      id: 1,
      name: "Family Promise of Spokane",
    },

    {
      id: 2,
      name: "Family Promise of Clarkz County",
    },

    {
      id: 3,
      name: "Family Promise of Clarkz County",
    },
  ];

  const navigation = useNavigation()

  return (
    <ScrollView>
      <Container>
        <Shelters>
          {dummyData.map((data) => (
            <Shelter name={data.name} id={data.id} navigation={navigation} />
          ))}
        </Shelters>
      </Container>
    </ScrollView>
  );
};

const Container = styled.View`
  width: 100%;

  display: flex;

  justify-content: center;

  align-items: center;
`;

const Shelters = styled.View`
  display: flex;

  width: 40%;

  ${props => props.theme.isLaptop && 
    'width: 45%;'
  }

  ${props => props.theme.isTablet && 
    'width: 70%;'
  }

  ${props => props.theme.isMobileL && 
    'width: 100%;'
  }

`;

const Shelter = ({ name, id, navigation }) => {
  return (
    <Card>
      <Card.Title>{name}</Card.Title>
      <Card.Divider />
      <Card.Image
        style={{ padding: 0 }}
        source={{
          uri: "https://awildgeographer.files.wordpress.com/2015/02/john_muir_glacier.jpg",
        }}
      />
      <Card.Divider />

      <Button
        icon={
          <Icon name="code" color="#ffffff" iconStyle={{ marginRight: 10 }} />
        }
        onPress={() => navigation.navigate('')}
        buttonStyle={{
          borderRadius: 0,
          marginLeft: 0,
          marginRight: 0,
          marginBottom: 0,
        }}
        title="VIEW NOW"
      />
    </Card>
  );
};

export default SheltersScreen;
