import { View, Text } from "react-native";
import styled from "styled-components/native";
import { MaterialCommunityIcons } from "@expo/vector-icons";
import React, { useState } from "react";

export function Drawer({ onChange,children }) {
  const [selected, setSelected] = useState(children[0].props.name)

  return (
    <DrawerContainer>
      <DrawerItems>
        {children.map(child => React.cloneElement(child, { onChange, selected, setSelected }))}
      </DrawerItems>
    </DrawerContainer>
  );
}

export const DrawerItem = ({
  icon,
  text,
  disabled = false,
  onChange = null,
  selected,
  setSelected,
  name,
}) => {
  return (
    <DrawerItemContainer onPress={() => {}} disabled={disabled}>
      <MaterialCommunityIcons
        name={icon}
        size={25}
        color={disabled ? "grey" : "black"}
      />

      {/* <Text>{text}</Text> */}
    </DrawerItemContainer>
  );
};

const DrawerContainer = styled.View`
  width: 50;
  height: 100%;
  position: fixed;
  background-color: #f2f7f6;
`;

const DrawerItems = styled.View`
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const DrawerItemContainer = styled.TouchableOpacity`
    margin-top: 10%;
    margin-bottom: 10%;
    padding: 12%;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;


    }
`;
