import { useState, useEffect } from 'react';

import { Dimensions } from 'react-native'

const window = Dimensions.get("window");
const screen = Dimensions.get("screen");


function useBreakpoints() {
    const [isDesktop, setIsDesktop ] = useState(false) 
    const [isTablet, setIsTablet ] = useState(false) 
    const [isMobile, setIsMobile ] = useState(false) 
    const [isMobileSm, setIsMobileSm ] = useState(false) 


    useEffect(() => {
      const subscription:any = Dimensions.addEventListener(
        "change",
        ({ window, screen }) => {

            switch(window.width) {
                case BREAKPOINT_XL:
                    setIsDesktop(true)

                    setIsTablet(false)
                    setIsMobile(false)

                case BREAKPOINT_MD:
                    setIsTablet(true)
                case BREAKPOINT_SM:
                    setIsMobile(true)
                case BREAKPOINT_XS:
                    setIsMobileSm(true)

            }

        }
      );

      return () => subscription?.remove();
    }, []);

    return { isDesktop, isMobile, isTablet, isMobileSm }

}

export default useBreakpoints

export const BREAKPOINT_XS = 320
export const BREAKPOINT_SM = 375
export const BREAKPOINT_MD = 768
export const BREAKPOINT_LG = 992
export const BREAKPOINT_XL = 1366
