import { View, Text } from "react-native";
import styled from "styled-components/native";
import { FontAwesome5 } from "@expo/vector-icons";

export default function Drawer() {
  return (
    <DrawerContainer>
      <DrawerItems>
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
        <DrawerItem icon='apple' text={'Navigation item'} />
      </DrawerItems>
    </DrawerContainer>
  );
}

const DrawerItem = ({ icon, text }) => {
  return (
    <DrawerItemContainer>
      <FontAwesome5 name={icon} size={30} color="white" />

      {/* <Text>{text}</Text> */}
    </DrawerItemContainer>
  );
};

const DrawerContainer = styled.View`
  width: 50;
  height: 100%;
  position: fixed;
  background-color: #F2F7F6;
`;

const DrawerItems = styled.View`
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const DrawerItemContainer = styled.TouchableOpacity`
    margin-bottom: 20%;

    flex: 1;
    justify-content: center;
    align-items: center;
    width: 100%;
    background-color: black;
`
