import { View, Text } from "react-native";

import React from "react";

import styled from "styled-components/native";

const list = [
  {
    title: "Appointments",
    icon: "av-timer",
  },
  {
    title: "Trips",
    icon: "flight-takeoff",
  },
];

const Index = () => {
  return (
    <View>
      <Container>
        <Reservations>
          {list.map((item, i) => (
            <ListItem title={"Full tilt holy fuck 123"} chevron />
          ))}
        </Reservations>
      </Container>
    </View>
  );
};

const ListItem = ({ title, chevron = false }) => {
  return (
    <ListItemContainer>
      <Text>{title}</Text>
    </ListItemContainer>
  );
};

const ListItemContainer = styled.View`
  display: flex;
  width: 100%;
  border: 1px solid lightgrey;
  justify-content: space-between;

  padding: 1rem;
`;

const Container = styled.View`
  width: 100%;

  display: flex;

  justify-content: center;

  align-items: center;
`;

const Reservations = styled.View`
  width: 80%;
`;

export default Index;
