import React, { useEffect } from "react";

import { Provider, useSelector } from "react-redux";
import { store } from "./state/store";

import { NavigationContainer, useNavigation } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";

import { HomeScreen, LoginScreen, RegisterScreen, IntakeScreen } from "./screens";

import EStyleSheet from "react-native-extended-stylesheet";
import Theme from "./Theme";
import { Button, NativeBaseProvider } from "native-base";
import { auth } from "./firebase";
import AsyncStorage from "@react-native-async-storage/async-storage";

export type RootStackParamList = {
  Home: undefined;
  Login: undefined;
  Register: undefined;
  Intake: undefined
};

declare global {
  namespace ReactNavigation {
    interface RootParamList extends RootStackParamList {}
  }
}

export default function App() {
  return (
    <Provider store={store}>
      <NavigationContainer>
        <NativeBaseProvider>
          <Theme>
            <SMS />
          </Theme>
        </NativeBaseProvider>
      </NavigationContainer>
    </Provider>
  );
}

function SMS() {

  let navigation = useNavigation();

  let { isLoggedIn } = useSelector((state:any) => state.user)

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(
      async (user) => {
        if (!user) {
          navigation.reset({
            index: 0,
            routes: [{ name: "Login" }],
          });

          await AsyncStorage.removeItem("accessToken");
        }

        if (user) {
          let { token } = await user.getIdTokenResult();

          await AsyncStorage.setItem("accessToken", token);

          navigation.reset({
            index: 0,
            routes: [{ name: "Home" }],
          });
        }
      }
    );

    return unsubscribe;
  }, []);

  const Stack = createNativeStackNavigator<RootStackParamList>();


  return (
    <Stack.Navigator 
    screenOptions={{
      headerStyle: {
        backgroundColor: '#8D4982',
      },
      headerTintColor: '#fff',
      headerTitleStyle: {
        fontWeight: 'bold',
      },

      headerRight: () => {
       return isLoggedIn ? <Button onPress={() => auth.signOut()}>Logout</Button> : ""
      }
    }}
    >
      <Stack.Screen name="Login" component={LoginScreen} />
      <Stack.Screen name="Register" component={RegisterScreen} />
      <Stack.Screen name="Home" component={HomeScreen} />
      <Stack.Screen name="Intake" component={IntakeScreen} />

    </Stack.Navigator>
  );
}
