import { View, Text } from "react-native";
import styled from "styled-components/native";
import { FontAwesome5 } from "@expo/vector-icons";

export function Drawer({children}) {
  return (
    <DrawerContainer>
      <DrawerItems>
        {children}
      </DrawerItems>
    </DrawerContainer>
  );
}

export const DrawerItem = ({ icon, text, disabled=false }) => {

  return (
    <DrawerItemContainer disabled={disabled}>
      <FontAwesome5 name={icon} size={25} color="black" />

      {/* <Text>{text}</Text> */}
    </DrawerItemContainer>
  );
};

const DrawerContainer = styled.View`
  width: 50;
  height: 100%;
  position: fixed;
  background-color: #F2F7F6;
`;

const DrawerItems = styled.View`
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const DrawerItemContainer = styled.TouchableOpacity`
    margin-top: 10%;
    margin-bottom: 10%;
    padding: 12%;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;


    }
`
