import React, { useEffect } from "react";


import { ThemeProvider } from "styled-components/native";
import useMediaQuery from "./hooks/useMediaQuery";


const Theme = ({ children }) => {
    const isLaptop = useMediaQuery({width: 1028})

    const isTablet = useMediaQuery({width: 768})

    const isMobile = useMediaQuery({width: 375})

    const isMobileS = useMediaQuery({width: 320})
    const isMobileL = useMediaQuery({width: 425})


    useEffect(() => {
        isLaptop
    }, [])

    const theme = {
      colors: {
        powderWhite: "#FFFDF9",
        persianGreen: "#06B49A",
        lightBlue: "#AFDBD2",
        onyx: "#36313D"
      },
      fonts: ["sans-serif", "Roboto"],
      fontSizes: {
        small: "1em",
        medium: "2em",
        large: "3em"
      },

      isMobile,
      isMobileS,
      isMobileL,
      isTablet,
      isLaptop
    };

  return <ThemeProvider theme={theme}>{children}</ThemeProvider>
};

export default Theme;
