import { StyleSheet, Text, View } from 'react-native'
import React, { useEffect } from 'react'
import { Container } from './HomeScreen.styles'
import { Button } from 'native-base'
import { auth } from '../../firebase'

import { useGetCurrentUserQuery } from '../../auth/users/useGetCurrentUserQuery'
import { useDispatch } from 'react-redux'
import { setCurrentUser } from '../../state/users/userActions'
import { useNavigation } from '@react-navigation/native'

export default function Index() {
  const [user, loading, error] = useGetCurrentUserQuery()

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(setCurrentUser(user))
  }, [user])

  if(loading) {
    return <View>Loading</View>
  }

  return (
    <Container>
      <Button onPress={() => auth.signOut()}>{`Sign out @ ${user.email}`}</Button>

      <RedirectUser role={user.role} />
    </Container>
  )
}

const RedirectUser = ({role}) => {
  switch(role) {
    default:
      return <Text>Something</Text>
  }
}

const RedirectTo = ({ screen }) => {
  const navigation = useNavigation()

  useEffect(() => {
    navigation.navigate(screen)
  }, [])
}
