import { View } from "react-native";

import React, { useEffect } from "react";

import styled from "styled-components/native";

import { MaterialCommunityIcons } from "@expo/vector-icons";

import { Text } from "native-base";
import { TouchableOpacity } from "react-native-gesture-handler";
import { useNavigation } from "@react-navigation/native";

import AsyncStorage from "@react-native-async-storage/async-storage";

import { axiosWithAuth } from "../../../../../auth/axiosWithAuth";

const list = [];

const Index = ({ shelterId }) => {
  const navigation = useNavigation();

  const [reservations, setReservations] = useState([]);

  const [loading, setLoading] = useState(false);

  const getReservations = async () => {
    setLoading(true)
    try {
      const token = await AsyncStorage.getItem("accessToken");

      let res = await axiosWithAuth(token).get(
        `/shelters/${shelterId}/reservations`
      );

      setReservations(res.data.reservations);
    } catch (error) {
      alert("error!");
    } finally {
      setLoading(false)
    }
  };

  useEffect(() => {
    getReservations()
  }, []);

  return (
    <View>
      <Container>
        <Reservations>
          {list.map((item, i) => (
            <ListItem
              navigation={navigation}
              id={item.id}
              hoh={item.hoh}
              beds={item.beds}
            />
          ))}
        </Reservations>
      </Container>
    </View>
  );
};

const ListItem = ({ hoh, beds, navigation, id }) => {
  const redirectToReservationDetails = () =>
    navigation.navigate("Reservation", { reservationId: id });

  return (
    <TouchableOpacity onPress={redirectToReservationDetails}>
      <ListItemContainer>
        <Text>{hoh + " / " + beds + " beds"}</Text>
        <MaterialCommunityIcons size={35} name="chevron-right" />
      </ListItemContainer>
    </TouchableOpacity>
  );
};

const ListItemContainer = styled.View`
  display: flex;

  cursor: pointer;

  flex-direction: row;

  align-items: center;

  width: 100%;
  border: 1px solid lightgrey;
  justify-content: space-between;

  padding: 1rem;
`;

const Container = styled.View`
  width: 100%;

  display: flex;

  justify-content: center;

  align-items: center;
`;

const Reservations = styled.View`
  margin-top: 3rem;

  margin-left: 5rem;

  width: 40%;

  ${(props) => props.theme.isLaptop && "width: 45%;"}

  ${(props) => props.theme.isTablet && "width: 70%;"}

  ${(props) => props.theme.isMobileL && "width: 95%;"}

  ${(props) => props.theme.isMobileS && "width: 98%;"}
`;

export default Index;
